// Loop through input items (n8n structure)
return items.map(item => {
  try {
    // 1. Access the nested text field from the Gemini/LLM response
    const rawContent = item.json.content.parts[0].text;

    // 2. Clean the output: Find the JSON object specifically
    // This Regex finds the first '{' and captures everything until the last '}'
    // It prevents errors if the AI adds text like "Here is your JSON:"
    const jsonMatch = rawContent.match(/\{[\s\S]*\}/);

    if (jsonMatch) {
      // 3. Parse the stringified JSON
      const parsedData = JSON.parse(jsonMatch[0]);

      // 4. Return clean, flat data for the next node
      return {
        json: {
          linkedinPost: parsedData.linkedinPost,
          imagePrompt: parsedData.imagePrompt,
          // Optional: Keep metadata if you need it
          // finishReason: item.json.finishReason
        }
      };
    } else {
      throw new Error("No JSON object found in text");
    }

  } catch (error) {
    // Error handling
    return {
      json: {
        error: error.message,
        rawInput: item.json.content ? item.json.content.parts[0].text : "Invalid Input Structure"
      }
    };
  }
});
